mapper.select:
select a.*,dt.dict_name as line_name, (case a.review_status when 'INIT' then '新建' when 'REJECT' then '被驳回' when 'APPROVAL' then '审批中' when 'APPROVED' then '已发布' end ) as status_name, dt.dict_name as LINE_NAME, u.actu_name as creator_name, uc.actu_name as reviewer_name from odr_construction_daily a left join wsd_base_dict dt on a.line = dt.dict_code and dt.TYPE_CODE = 'line' left join wsd_sys_user u on a.creator=u.id left join wsd_sys_user uc on a.reviewer=uc.id
AND a.line =#{line}
AND a.id =#{id}
AND a.RECORD_TIME = str_to_date(#{recordTime},'%Y-%m-%d')
AND a.REVIEW_STATUS !=#{checkReviewStatus}
AND a.REVIEW_STATUS =#{reviewStatus}
AND a.id in
#{item}
AND a.line in
#{item}
AND a.RECORD_TIME <= str_to_date(#{endTime},'%Y-%m-%d')
AND a.RECORD_TIME >= str_to_date(#{startTime},'%Y-%m-%d')
order by a.RECORD_TIME desc

select SUM(a.weekly_actual_construction_quantity) as weekly_actual_construction_quantity, SUM(a.daily_plan_construction_quantity) AS daily_plan_construction_quantity, SUM(a.daily_actual_construction_quantity) as daily_actual_construction_quantity, SUM(a.temp_plan_construction_quantity) AS temp_plan_construction_quantity, SUM(a.temp_actual_construction_quantity) as temp_actual_construction_quantity, SUM(a.repair_plan_construction_quantity) AS repair_plan_construction_quantity, SUM(a.repair_actual_construction_quantity) as repair_actual_construction_quantity from odr_construction_daily a
a.REVIEW_STATUS ="APPROVED"
AND a.line =#{line}
AND date_format(a.RECORD_TIME,'%Y-%m')=#{recordTime2Month}

select SUM(a.WEEKLY_PLAN_CONSTRUCTION_QUANTITY) as WEEKLY_PLAN_CONSTRUCTION_QUANTITY
from odr_construction_daily a

    a.REVIEW_STATUS ="APPROVED"

        AND a.line =#{line}


         and date_format(a.RECORD_TIME,'%Y-%m-%d')in

            #{item}
