mapper.select:
select a.id,a.LINE as line,dt.dict_name as lineName,
a.record_day as recordDay,a.major_vehicle as majorVehicle,a.major_power as majorPower,
a.major_signal as majorSignal,a.major_communication as majorCommunication,a.major_construction as majorConstruction,
a.major_mechatronics as majorMechatronics,a.major_afc as majorAfc,a.major_other as majorOther,a.status as status
from odr_fault_daily a
left join wsd_base_dict dt on a.line = dt.dict_code and dt.TYPE_CODE = 'line'
where 1=1

        AND a.LINE =#{line}


        AND a.LINE in

            #{item}



        AND a.STATUS =#{status}


        AND a.RECORD_DAY = #{recordDay}


        AND a.id = #{id}


        AND a.id in

            #{item}



        AND a.RECORD_DAY   <=  #{endTime}


        AND a.RECORD_DAY   >=  #{startTime}

order by a.RECORD_DAY desc

SELECT a.LINE as line, date_format(a.RECORD_DAY,'%Y-%m') as recordMonth, SUM(a.major_vehicle) AS majorVehicle, SUM(a.MAJOR_POWER) AS majorPower, SUM(a.MAJOR_signal) AS majorSignal, SUM(a.MAJOR_communication) AS majorCommunication, SUM(a.MAJOR_construction) AS majorConstruction, SUM(a.MAJOR_Mechatronics) AS majorMechatronics, SUM(a.MAJOR_AFC) AS majorAfc, SUM(a.MAJOR_OTHER) AS majorOther FROM odr_fault_daily a where a.status = 'APPROVED' and a.RECORD_DAY BETWEEN #{startTime} AND #{endTime}
AND a.id =#{id}
AND a.LINE =#{line}
AND a.id in
#{item}
GROUP BY a.LINE
